import React, { useState } from "react";

import TodoList from "./TodoList";
import Ongoing from "./Ongoing";
import Completed from "./Completed";

const Main = (props) => {
  const [todoList, setTodoList] = useState("");
  const [updateList, setUpdateList] = useState([]);

  const handleTodoTasks = (event) => {
    event.preventDefault();
    const printTasks = { todoList };
    setUpdateList({ todoList });
    props.addToNewTasks(printTasks);
  };

  const handleTodoList = (event) => {
    setTodoList(event.target.value);
    console.log(event.target.value);
  };

  return (
    <div>
      <h1>Home</h1>
      <TodoList
        handleTodoList={handleTodoList}
        handleTodoTasks={handleTodoTasks}
        tasks={props.tasks}
        updateList={updateList}
      />
      <Ongoing />
      <Completed />

      <div className="todoContainer">
        <h1>ToDoList</h1>
        <form onSubmit={props.handleTodoTasks}>
          <input
            type="text"
            placeholder="Add a new task..."
            className="todoAdd"
            onChange={props.handleTodoList}
            value={todoList}
          />
          <button type="submit">add</button>
